export default class Random {
    private rng;
    constructor(seedValue?: string);
    randomInteger(min: number, max: number, { onlyOdd, onlyEven }?: {
        onlyOdd?: boolean | undefined;
        onlyEven?: boolean | undefined;
    }): number;
    randomEvenInteger(min: number, max: number): number;
    randomOddInteger(min: number, max: number): number;
    randomPick<T>(array: T[]): T;
}
